for _, t := range b.Transactions {
		logger.Infof("T Type:%s\n", t.Type)
		ccID := &amp;pb.ChaincodeID{}
		err := proto.Unmarshal(t.ChaincodeID, ccID)
		if err != nil {
			logger.Infof("extract ccid error, %+v\n", err)
		} else {
			logger.Infof("T CCID:%+v\n", ccID)
		}

		cis := &amp;pb.ChaincodeInvocationSpec{}
		err = proto.Unmarshal(t.Payload, cis)
		if err != nil {
			logger.Infof("extract payload error, %+v\n", err)
		} else {
			logger.Infof("T Payload:%+v\n", cis)
		}

