`function deploychaincode(err, deployuser) {

	var deployRequest = {
		fcn: "init",
		args: ["50", "300"],
		chaincodePath: "settlement/"
	};

	var deployTx = deployuser.deploy(deployRequest);

	deployTx.on("complete", function(results){
		chaincodeID = results.chaincodeID;
		console.log("\nChaincode ID: " + chaincodeID);
		console.log(util.format("\n Successfully deployed chaincode\nrequest = %j\n response = %j", deployRequest, results));
		fs.writeFileSync(chaincodeIDPath, chaincodeID);
	});

	deployTx.on("error", function(err) {
		console.log(util.format("\nFailed to deploy chaincode\nrequest = %j\n error = %j", deployRequest, err));
	});
}`

